    private void AjouterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AjouterActionPerformed
       if (valid()){
                   l.add(txtisbn.getText(), txtTitre.getText(), txtAuteur.getText(), txtDesc.getText());
       }else{
               JOptionPane.showMessageDialog(null,"form inalide");
       }
        Listing();
         clear();       
    }//GEN-LAST:event_AjouterActionPerformed

