    private void tableMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tableMouseClicked
       int row = table.getSelectedRow();
       int id=Integer.parseInt(table.getModel().getValueAt(row, 0).toString());
       String isbn= table.getModel().getValueAt(row, 1).toString();
       String titre= table.getModel().getValueAt(row, 3).toString();
       String Auteur= table.getModel().getValueAt(row, 2).toString();
       String desc= table.getModel().getValueAt(row, 4).toString();
       txtID.setText(id+"");
        txtisbn.setText(isbn);
         txtTitre.setText(titre);
          txtAuteur.setText(Auteur);
           txtDesc.setText(desc);
 
    }//GEN-LAST:event_tableMouseClicked

