private LivreControleur l=new LivreControleur();
public void Listing(){
       ArrayList<Livre> tab=l.getAll();
       DefaultTableModel dm = new DefaultTableModel(null,new String[]{"ID","ISBN","Auteur","Titre","Description"});
       dm.setNumRows(0);
       for(Livre livre:tab){
           Object[] m={livre.getId(),livre.getIsbn(),livre.getAuteur(),livre.getTitre(),livre.getDescription()};
           dm.addRow(m);
       }
       table.setModel(dm);

}
//fonction pour clirer formulaire
public void clear(){
    txtID.setText("");
    txtisbn.setText("");
    txtTitre.setText("");
    txtAuteur.setText("");
    txtDesc.setText("");
}
private boolean valid(){
  if(txtisbn.getText().trim().equals("") 
          ||txtTitre.getText().trim().equals("")
          || txtAuteur.getText().trim().equals("")
           ||txtDesc.getText().trim().equals("")){
    return false;
    
}
  return true;
       
}
  private void found(String text) {
        ArrayList<Livre> livre;
        livre=l.search(text);
        DefaultTableModel model =(DefaultTableModel)table.getModel();
        model.setNumRows(0);
        for(Livre a:livre){
           Object[] m={a.getId(),a.getIsbn(),a.getAuteur(),a.getTitre(),a.getDescription()};
        model.addRow(m);
    }
   }
  
